import React, { Component } from "react";
import CategoryList from './CategoryList';
import Navi from './Navi';
import ProductList from './ProductList'
import {Container,Row,Col} from "reactstrap"

export default class App extends Component {
  state={currentCategory:"",products:[]}

  componentDidMount() {
    this.getProducts();
  }

  changeCategory = (category) => {
    this.setState({ currentCategory: category.categoryName });
    this.getProducts(category.seoUrl);
  };

  getProducts = () => {
    let url="http://localhost:3000/products";
    if(seoUrl){
      url+="/"+seoUrl;
    }
    fetch(url)
      .then((response) => response.json())
      .then((data) => this.setState({ products: data }));
  };

  render() {
    let productInfo={title:"Product List",baskabisey:"baska"};
    let categoryInfo={title:"Category List"};

    return (
      <div>
        <Container>
          <Row>
            <Navi></Navi>
          </Row>
          <Row>
            <Col xs="3">
              <CategoryList currentCategory={this.state.currentCategory} changeCategory={this.changeCategory} info={categoryInfo}></CategoryList>
            </Col>
            <Col xs="9">
              <ProductList 
              products={this.state.products}
              currentCategory={this.state.currentCategory} 
              info={productInfo}
              ></ProductList>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
